import "./App.css";

import { Circle, MapContainer, Popup, TileLayer } from "react-leaflet";

import { LatLngExpression } from "leaflet";
import React from "react";
import { usePapaParse } from "react-papaparse";

function App() {
  const bufferRadius = 20000;
  const fillOpacity = 0.5;
  const strokeOpacity = 0;
  const mapCenter: LatLngExpression = [13, 120];
  const zoom = 7;
  const maxZoom = 10;
  const minZoom = 6;
  let initialLoad = true;
  const { readRemoteFile } = usePapaParse();
  const [stations, setStations] = React.useState<any>();

  const url =
    "https://docs.google.com/spreadsheets/d/e/2PACX-1vQcuVE7J9YLh5q8cy0QlFVJCKQ1VTxwywwVmtKA52vWIf-I62jCL0NrFnyEHGdLbPqXrqN7Y8rUtCT-/pub?output=csv";

  readRemoteFile(url, {
    complete: (results) => {
      console.log(initialLoad);
      results.data.shift();
      if (initialLoad) {
        // setStations(results.data);
        initialLoad = false;
      }
      setTimeout(() => console.log("Initial timeout!"), 10000);
      console.log(initialLoad);
    },
    download: true,
  });

  return (
    <div className="App">
      {stations}
      <MapContainer
        center={mapCenter}
        minZoom={minZoom}
        zoom={zoom}
        maxZoom={maxZoom}
      >
        <TileLayer
          attribution='&copy; <a href="https://www.openstreetmap.org/copyright">OpenStreetMap</a> contributors'
          url="https://server.arcgisonline.com/ArcGIS/rest/services/World_Imagery/MapServer/tile/{z}/{y}/{x}"
        />

        {/* loop through array and declare a circle for each row */}

        <Circle
          center={[13.2, 120]}
          pathOptions={getColor(0.1)}
          radius={bufferRadius}
          opacity={strokeOpacity}
          fillOpacity={fillOpacity}
        >
          <Popup>attributes</Popup>
        </Circle>
      </MapContainer>
    </div>
  );
}

function getColor(rain: number) {
  var colors = ["#38B75F", "#FCFD31", "#FFBD59", "#FE0100", "#313D39"];

  const threshold = [0.1, 0.2, 0.3, 0.4];
  let index = 4;

  if (rain <= threshold[0]) {
    index = 0;
  } else if (rain > threshold[0] && rain <= threshold[1]) {
    index = 1;
  } else if (rain > threshold[1] && rain <= threshold[2]) {
    index = 2;
  } else if (rain > threshold[2] && rain <= threshold[3]) {
    index = 3;
  }

  return { fillColor: colors[index], color: colors[index] };
}

export default App;
