import "./App.css";

import { Circle, MapContainer, Popup, TileLayer } from "react-leaflet";

import Box from "@mui/material/Box";
import { LatLngExpression } from "leaflet";
import Paper from "@mui/material/Paper";
import React from "react";
import Slide from "@mui/material/Slide";
import { useEffect } from "react";
import { usePapaParse } from "react-papaparse";

function App() {
  const containerRef = React.useRef(null);
  const bufferRadius = 20000;
  const fillOpacity = 0.5;
  const strokeOpacity = 0;
  const mapCenter: LatLngExpression = [13, 120];
  const zoom = 7;
  const maxZoom = 10;
  const minZoom = 6;
  let initialLoad = true;

  const { readRemoteFile } = usePapaParse();
  const [stations, setStations] = React.useState<any>([]);
  const [checked, setChecked] = React.useState(false);

  const url =
    "https://docs.google.com/spreadsheets/d/e/2PACX-1vQcuVE7J9YLh5q8cy0QlFVJCKQ1VTxwywwVmtKA52vWIf-I62jCL0NrFnyEHGdLbPqXrqN7Y8rUtCT-/pub?output=csv";

  useEffect(() => {
    readRemoteFile(url, {
      complete: (results) => {
        console.log(initialLoad);
        results.data.shift();
        setStations(results.data);
      },
      download: true,
    });
  }, []);

  return (
    <div className="App">
      <Box ref={containerRef}>
        <MapContainer
          style={{ zIndex: 4 }}
          center={mapCenter}
          minZoom={minZoom}
          zoom={zoom}
          maxZoom={maxZoom}
        >
          <TileLayer
            attribution='&copy; <a href="https://www.openstreetmap.org/copyright">OpenStreetMap</a> contributors'
            url="https://server.arcgisonline.com/ArcGIS/rest/services/World_Imagery/MapServer/tile/{z}/{y}/{x}"
          />

          {stations.map((value: any, index: any) => {
            let center: LatLngExpression = [
              parseFloat(value[3]),
              parseFloat(value[4]),
            ];
            let stationName = value[0];
            let date = value[1];
            let status = value[2];
            let elevation = value[5];
            let currentRain = parseFloat(value[14]);
            let town = value[7];
            let municipality = value[8];
            let province = value[9];

            return (
              <Circle
                key={index}
                center={center}
                pathOptions={getColor(currentRain)}
                radius={bufferRadius}
                opacity={strokeOpacity}
                fillOpacity={fillOpacity}
                eventHandlers={{
                  click: (e) => {
                    highlightFeature(e);
                  },
                  mouseout: (e) => {
                    removehighlightFeature(e, currentRain);
                  },
                }}
              >
                <Popup>
                  <span style={{ fontWeight: 600, fontSize: "1.1rem" }}>
                    {stationName}
                  </span>
                  <br></br>
                  Reading Date: {date}
                  <br></br>
                  Status: {status}
                  <br></br>
                  Elevation: {elevation}
                  <br></br>
                  Current Rainfall: {currentRain}mm
                  <br></br>
                  Location: {town}, {municipality}, {province}
                </Popup>
              </Circle>
            );
          })}
        </MapContainer>
        <Slide
          direction="up"
          in={checked}
          container={containerRef.current}
          style={{ zIndex: 99999 }}
        >
          <Paper sx={{ width: "50px", height: "50px" }} elevation={3}>
            aaaa
          </Paper>
        </Slide>
      </Box>
    </div>
  );

  function highlightFeature(event: any) {
    setChecked((prev) => !prev);

    let layer = event.sourceTarget;
    layer.setStyle({
      color: "yellow",
      opacity: 1,
    });
  }

  function removehighlightFeature(event: any, rain: any) {
    setChecked((prev) => !prev);
    console.log(event);
    let layer = event.sourceTarget;
    layer.setStyle(getColor(rain));
  }

  function getColor(rain: number) {
    var colors = ["#38B75F", "#FCFD31", "#FFBD59", "#FE0100", "#313D39"];

    const threshold = [0.1, 0.2, 0.3, 0.4];
    let index = 4;

    if (rain <= threshold[0]) {
      index = 0;
    } else if (rain > threshold[0] && rain <= threshold[1]) {
      index = 1;
    } else if (rain > threshold[1] && rain <= threshold[2]) {
      index = 2;
    } else if (rain > threshold[2] && rain <= threshold[3]) {
      index = 3;
    }

    return { fillColor: colors[index], color: colors[index], opacity: 0 };
  }
}

export default App;
